import React, { useState, useEffect } from "react";
import Footer from "../Components/footer";
import Navbar from "../Components/navbar";
import photo1 from "../assets/image6.jpg";  
import photo2 from "../assets/image2.jpg"; 
import photo3 from "../assets/image3.png";

const images = [photo1, photo2, photo3];  // Include all three images

const Home = () => {
    const [visibleImageIndex, setVisibleImageIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setVisibleImageIndex((prevIndex) =>
                prevIndex < images.length - 1 ? prevIndex + 1 : 0
            );
        }, 3000); // Smooth transition every 3 seconds

        return () => clearInterval(interval);
    }, []);

    return (
        <>
            <div className="min-h-screen bg-gradient-to-r from-indigo-100 via-purple-100 to-pink-100">
                <Navbar />

                <div className="flex flex-col lg:flex-row items-center justify-between px-10 py-10 gap-10">
                    <div className="lg:w-1/2 text-center lg:text-left">
                        <h1 className="text-6xl font-bold text-gray-900 mb-6 font-serif leading-tight">
                            Hi, I'm <span className="text-purple-500">Neha Shah</span>
                        </h1>
                        <p className="text-xl text-gray-800 font-light max-w-xl">
                            Welcome to my world! Here you will find creative solutions, innovative designs, and inspiring content that reflects my passion for growth, technology, and creativity.
                        </p>
                    </div>

                    <div className="lg:w-[700px] w-full h-[800px] overflow-hidden transition duration-1000 ease-in-out">
                        <img
                            src={images[visibleImageIndex]}
                            alt={`Slide ${visibleImageIndex + 1}`}
                            className="w-full h-full object-cover rounded-xl shadow-2xl transition-all duration-1000"
                        />
                    </div>
                </div>

                <Footer />
            </div>
        </>
    );
};

export default Home;
