import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Navbar from "../Components/navbar";
import Footer from "../Components/footer";
import photo1 from "../assets/image6.jpg"; // Example photo
import photo2 from "../assets/JavaScript frameworks-bro.png"; // Example photo
import photo3 from "../assets/image3.png"; // Example photo

const images = [photo1, photo2, photo3]; // Array of images

const ReactPage = () => {
  const { state } = useLocation();

  if (!state) return <p className="text-center py-20">No data found.</p>;

  const [visibleImageIndex, setVisibleImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setVisibleImageIndex((prevIndex) =>
        prevIndex < images.length - 1 ? prevIndex + 1 : 0
      );
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const imageToShow = state.image || images[visibleImageIndex]; // Default to dynamic image

  return (
    <>
      <Navbar />
      <div className="bg-gray-50 dark:bg-gray-900 min-h-screen py-16 px-6">
        <div className="max-w-4xl mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8">
          <h1 className="text-4xl font-bold text-gray-800 dark:text-white mb-4">
            {state.title || state.degree}
          </h1>
          <p className="text-lg text-gray-600 dark:text-gray-300 mb-2">
            {state.organization || state.institution} ({state.year})
          </p>
          <p className="text-gray-700 dark:text-gray-300 mb-4">{state.description}</p>
          
          {/* Display dynamic image */}
          <img
            src={imageToShow}
            alt="Training/Education"
            className="rounded-lg mt-4 w-full object-cover max-h-[400px]"
          />
        </div>
      </div>
      <Footer />
    </>
  );
};

export default ReactPage;
