import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import Navbar from "../Components/navbar";
import Footer from "../Components/footer";
import photo1 from "../assets/image6.jpg";
import photo2 from "../assets/React.png"; // fallback image for training
import photo3 from "../assets/image3.png";

const images = [photo1, photo3];

const ReactPage = () => {
  const { state } = useLocation();
  const [visibleImageIndex, setVisibleImageIndex] = useState(0);

  useEffect(() => {
    if (!state) {
      const interval = setInterval(() => {
        setVisibleImageIndex((prevIndex) =>
          prevIndex < images.length - 1 ? prevIndex + 1 : 0
        );
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [state]);

  // Show slideshow if no training info is passed
  if (!state) {
    return (
      <>
        <Navbar />
        <div className="bg-gray-50 dark:bg-gray-900 min-h-screen py-16 px-6 flex justify-center items-center">
          <div className="relative w-[280px] h-[420px] lg:w-[340px] lg:h-[460px] overflow-hidden rounded-xl shadow-2xl">
            <img
              src={images[visibleImageIndex]}
              alt="Hero"
              className="w-full h-full object-cover transition-all duration-1000 rounded-xl"
            />
            <div className="absolute top-0 left-0 right-0 bottom-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-30"></div>
          </div>
        </div>
        <Footer />
      </>
    );
  }

  // Show training info if state is passed
  const imageToShow = state.image || photo2;

  return (
    <>
      <Navbar />
      <div className="bg-gray-50 dark:bg-gray-900 min-h-screen py-16 px-6">
        <div className="max-w-4xl mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8">
          <h1 className="text-4xl font-bold text-gray-800 dark:text-white mb-4">
            {state.title || state.degree}
          </h1>
          <p className="text-lg text-gray-600 dark:text-gray-300 mb-2">
            {state.organization || state.institution} ({state.year})
          </p>
          <p className="text-gray-700 dark:text-gray-300 mb-4">{state.description}</p>

          <img
            src={imageToShow}
            alt="Training"
            className="rounded-lg mt-4 w-full object-cover max-h-[none]"
          />
        </div>
      </div>
      <Footer />
    </>
  );
};

export default ReactPage;
