import React, { useState, useEffect } from "react";
import Footer from "../Components/footer";
import Navbar from "../Components/navbar";
import photo1 from "../assets/image6.jpg";  
import photo2 from "../assets/image2.jpg"; 
import photo3 from "../assets/image3.png";

const images = [photo1, photo2];  

const Home = () => {
    const [visibleImageIndex, setVisibleImageIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setVisibleImageIndex((prevIndex) => {
                return prevIndex < images.length - 1 ? prevIndex + 1 : 0;
            });
        }, 2000); 

        return () => clearInterval(interval); 
    }, []);

    return (
        <>
            <div>
                <Navbar />
               
                <div className="flex items-center justify-between px-10 py-2">
                    <div className="w-1/2">
                        <h1 className="text-5xl font-extrabold text-gray-900 mb-4 tracking-wide">
                            Hi, I'm <span className="text-purple-400">Neha Shah</span>
                        </h1>
                        <p className="text-xl font-medium text-gray-700">
                            Welcome to my world! Here you will find creative solutions, innovative designs, and inspiring content that reflects my passion for growth, technology, and creativity.
                        </p>
                    </div>

                    <div className="w-[800px]">
                        <img
                            src={images[visibleImageIndex]}
                            alt={`Image ${visibleImageIndex + 1}`}
                             className="w-full h-auto rounded-lg shadow-lg"
                         

                        />
                    </div>
                </div>

                <Footer />
            </div>
        </>
    );
};

export default Home;
