import React, { useState, useEffect } from "react";
import Footer from "../Components/footer";
import Navbar from "../Components/navbar";
import photo1 from "../assets/image6.jpg";  
import photo2 from "../assets/image2.jpg"; 
import photo3 from "../assets/image3.png";

const images = [photo1,  photo3];  // Include all three images

const Home = () => {
    const [visibleImageIndex, setVisibleImageIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setVisibleImageIndex((prevIndex) =>
                prevIndex < images.length - 1 ? prevIndex + 1 : 0
            );
        }, 3000);

        return () => clearInterval(interval);
    }, []);

    return (
        <>
      <div className="min-h-screen bg-gradient-to-r from-blue-100 via-indigo-200 to-teal-200">
  <Navbar />


  <div className="flex flex-col lg:flex-row items-center justify-between px-10 py-20 gap-12">
 
    <div className="lg:w-1/2 text-center lg:text-left space-y-6">
      <h1 className="text-5xl font-extrabold text-gray-800 font-serif">
        Hi, I'm <span className="text-teal-500">Neha Shah</span>
      </h1>
      <p className="text-2xl text-gray-700">
        Welcome to my world! I create, innovate, and inspire through tech and design. Dive in to discover more.
      </p>
      <p className="text-xl text-gray-600">
        Passionate about growth, creativity, and technology. Let's explore new ideas and bring them to life!
      </p>
      <a
        href="#contact"
        className="inline-block mt-6 px-6 py-3 text-white bg-gradient-to-r from-teal-500 to-indigo-500 rounded-full font-semibold hover:scale-105 transition-transform"
      >
        Let's Connect
      </a>
    </div>

    {/* Right: Image */}
    <div className="lg:w-1/2 flex justify-center">
      <div className="w-[300px] h-[500px] lg:w-[400px] lg:h-[500px] overflow-hidden rounded-xl shadow-2xl">
        <img
          src={images[visibleImageIndex]}
          alt="Hero"
          className="w-full h-full object-cover transition-all duration-1000 rounded-xl"
        />
      </div>
    </div>
  </div>

  {/* Below Section: Fun Facts */}
  {/* <div className="bg-gradient-to-r from-indigo-300 via-teal-300 to-blue-400 py-16 px-8">
    <h2 className="text-4xl font-bold text-white text-center mb-10">A Little About Me</h2>

    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-6xl mx-auto">
      {[
        { count: "10+", label: "Years of Experience", color: "text-teal-600" },
        { count: "100+", label: "Projects Completed", color: "text-indigo-600" },
        { count: "50+", label: "Clients Served", color: "text-yellow-500" },
        { count: "5+", label: "Awards Won", color: "text-pink-500" },
      ].map((fact, i) => (
        <div
          key={i}
          className="bg-white p-6 rounded-xl shadow-lg text-center hover:shadow-2xl transition-shadow"
        >
          <h3 className={`text-3xl font-bold ${fact.color}`}>{fact.count}</h3>
          <p className="text-lg text-gray-700">{fact.label}</p>
        </div>
      ))}
    </div>
  </div> */}
  {/* Below Section: Fun Facts */}
  <div className="bg-gradient-to-r from-indigo-100 via-teal-100 to-blue-100 py-16 px-8">
    <h2 className="text-4xl font-bold text-gray-800 text-center mb-10">More About Me</h2>

    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-6xl mx-auto">
      {[
        { count: "3+", label: "College Projects", color: "text-teal-500" },
        { count: "2", label: "Hackathons Participated", color: "text-indigo-500" },
        { count: "4.0", label: "GPA Achieved", color: "text-green-600" },
        { count: "5+", label: "Clubs & Events Involved", color: "text-pink-500" },
      ].map((fact, i) => (
        <div
          key={i}
          className="bg-white p-6 rounded-xl shadow-md text-center hover:shadow-xl transition-shadow"
        >
          <h3 className={`text-3xl font-bold ${fact.color}`}>{fact.count}</h3>
          <p className="text-md text-gray-600 mt-2">{fact.label}</p>
        </div>
      ))}
    </div>
  </div>

  <Footer />
</div>

        </>
    );
};

export default Home;
