import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import Footer from "../Components/footer";
import Navbar from "../Components/navbar";
import photo1 from "../assets/image6.jpg";  
import photo2 from "../assets/JavaScript frameworks-bro.png"; // Featured illustration
import photo3 from "../assets/image3.png";

// Only rotating these two
const images = [photo1, photo3];

const Home = () => {
  const [visibleImageIndex, setVisibleImageIndex] = useState(0);
  const [isLeftSection, setIsLeftSection] = useState(true); // This will control the dynamic layout

  useEffect(() => {
    const interval = setInterval(() => {
      setVisibleImageIndex((prevIndex) =>
        prevIndex < images.length - 1 ? prevIndex + 1 : 0
      );
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 via-indigo-200 to-teal-200">
      <Navbar />

      {/* Hero Section */}
      <div className="flex flex-col lg:flex-row items-center justify-between px-10 py-32 gap-20 min-h-[100vh]">
        <motion.div
          className="lg:w-1/2 text-center lg:text-left space-y-6"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <h1 className="text-5xl font-extrabold text-gray-800 font-serif">
            Hi, I'm <span className="text-teal-500">Neha Shah</span>
          </h1>
          <p className="text-2xl text-gray-700">
            Welcome to my world! I create, innovate, and inspire through tech and design. Dive in to discover more.
          </p>
          <p className="text-xl text-gray-600">
            Passionate about growth, creativity, and technology. Let's explore new ideas and bring them to life!
          </p>
          <a
            href="#contact"
            className="inline-block mt-6 px-6 py-3 text-white bg-gradient-to-r from-teal-500 to-indigo-500 rounded-full font-semibold hover:scale-105 transition-transform"
          >
            Let's Connect
          </a>
        </motion.div>

        <motion.div
          className="lg:w-1/2 flex justify-center"
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="w-[280px] h-[420px] lg:w-[340px] lg:h-[460px] overflow-hidden rounded-xl shadow-2xl">
            <img
              src={images[visibleImageIndex]}
              alt="Hero"
              className="w-full h-full object-cover transition-all duration-1000 rounded-xl"
            />
          </div>
        </motion.div>
      </div>

      {/* Dynamic Left and Right Section */}
      <motion.div
        className={`flex flex-col lg:flex-row items-center justify-center py-16 px-4 bg-[#1E2A47]`} // Soft dark blue
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 1 }}
      >
        {/* Left or Right Section Text Based on State */}
        {isLeftSection ? (
          <div className="lg:w-1/3 text-center lg:text-left mb-6 lg:mb-0 lg:mr-8">
            <h3 className="text-xl font-semibold text-gray-100 mb-4">
              Empowering Ideas with Modern Web Technologies
            </h3>
            <p className="text-lg text-gray-200">
              Leveraging cutting-edge tools to create scalable solutions. 
              Whether it's front-end design or back-end architecture, 
              I believe in continuous learning and adapting to new challenges.
            </p>
          </div>
        ) : (
          <div className="lg:w-1/3 text-center lg:text-right mb-6 lg:mb-0 lg:ml-8">
            <h3 className="text-xl font-semibold text-gray-100 mb-4">
              Bringing Ideas to Life
            </h3>
            <p className="text-lg text-gray-200">
              By integrating creativity with technology, I strive to bring innovative solutions that solve real-world problems. 
              Join me in creating something meaningful.
            </p>
          </div>
        )}

        {/* Centered Image */}
        <div className="lg:w-1/3 flex justify-center mb-6 lg:mb-0">
          <img
            src={photo2}
            alt="Tech Illustration"
            className="w-[500px] h-auto object-contain rounded-xl shadow-lg"
          />
        </div>

        {/* Toggle for Left/Right Dynamic Sections */}
        <button
          onClick={() => setIsLeftSection((prev) => !prev)} // Toggles between Left and Right section
          className="absolute top-16 right-10 px-4 py-2 bg-teal-500 text-white rounded-full font-semibold hover:bg-teal-600 transition-colors"
        >
          Toggle Layout
        </button>
      </motion.div>

      <Footer />
    </div>
  );
};

export default Home;
