import React, { useState, useEffect } from "react";
import Footer from "../Components/footer";
import Navbar from "../Components/navbar";


const images = [
    "/assets/image.pn",
];

const Home = () => {
    const [visibleImageIndex, setVisibleImageIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setVisibleImageIndex((prevIndex) => {
                return prevIndex < images.length - 1 ? prevIndex + 1 : 0;
            });
        }, 2000);
        return () => clearInterval(interval);
    }, []);

    return (
        <>
            <div>
                <Navbar />
                <br />
                <br />
                <br />
                <br />
                <br />

                {/* Flex container for the image and description */}
                <div className="flex items-center justify-between px-10 py-6">
                    {/* Description on the left */}
                    <div className="w-1/2">
                        <h1 className="text-4xl font-bold mb-4">Your Description Here</h1>
                        <p className="text-lg">
                            Add your description or any other text here. You can describe the
                            image or add any content that fits your needs.
                        </p>
                    </div>

                    {/* Image on the right */}
                    <div className="w-1/2">
                        <img
                            src={images[visibleImageIndex]}
                            alt={`Image ${visibleImageIndex + 1}`}
                            className="w-full h-auto"
                        />
                    </div>
                </div>

                <Footer />
            </div>
        </>
    );
};

export default Home;
